#include "inc.h"

class Solution2 {
    //Another way of implementing Trie
public:
    int m, n;
    struct Node{
        string str;
        Node* child[26];
        Node(){
            str = "";
            for(int i = 0; i < 26; ++i)
                child[i] = NULL;
            return;
        }
    };
    void insert(Node* root, string s){
        for(char ch : s){
            if(root->child[ch-'a'] == NULL)
                root->child[ch-'a'] = new Node();
            root = root->child[ch-'a'];
        }
        root->str = s;
    }
    Node* buildTrie(vector<string>& words){
        Node* root = new Node();
        for(auto word: words){
            insert(root, word);
        }
        return root;
    }
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        Node* root = buildTrie(words);
        vector<string> ans;
        if(board.empty() || board[0].empty())
            return ans;
        m = (int)board.size();
        n = (int)board[0].size();
        for(int i = 0; i < m; ++i){
            for(int j = 0; j < n; ++j){
                DFS(board, i, j, root, ans);
            }
        }
        return ans;
    }
    void DFS(vector<vector<char>>& board, int i, int j, Node* root, vector<string>& ans){
        char ch = board[i][j];
        if(ch == '#' || root->child[ch - 'a'] == NULL)
            return;
        root = root->child[ch - 'a'];
        if(!root->str.empty()){
            ans.push_back(root->str);
            root->str = "";
        }
        board[i][j] = '#';
        if(i > 0)
            DFS(board, i - 1, j, root, ans);
        if(i < m - 1)
            DFS(board, i + 1, j, root, ans);
        if(j > 0)
            DFS(board, i, j - 1, root, ans);
        if(j < n - 1)
            DFS(board, i, j + 1, root, ans);
        board[i][j] = ch;
    }
};



class Solution {
public:
    int height, width;
    struct Trie{
        char val;
        Trie* child = NULL;
        Trie* next = NULL;
        Trie(char c){
            val = c;
        }
    };
    
    void addWord(Trie* root, string word){
        if(word.empty())
            return;
        word += '$';
        for(char c : word){
            Trie* pre = root;
            Trie* parent = root;
            root = root->child;
            while(root){
                if(root->val == c){
                    break;
                }
                pre = root;
                root = root->next;
            }
            if(!root){
                if(pre == parent){
                    pre->child = new Trie(c);
                    root = pre->child;
                }else{
                    pre->next = new Trie(c);
                    root = pre->next;
                }
            }
        }
    }
    
    bool DFS(vector<vector<char>>& board, vector<vector<bool>>& visited, vector<string>& result, Trie* root, string current, int i, int j){
        if(board[j][i] != root->val){
            return false;
        }else{
            current += root->val;
            visited[j][i] = true;
            Trie* pre = root;
            Trie* cur = root->child;
            while(cur){
                bool solved = false;
                if(cur->val == '$'){
                    solved = true;
                    result.push_back(current);
                }
                if(!solved && i < width - 1 && !visited[j][i + 1])
                    solved = DFS(board, visited, result, cur, current, i + 1, j);
                if(!solved && i > 0 && !visited[j][i - 1])
                    solved = DFS(board, visited, result, cur, current, i - 1, j);
                if(!solved && j < height - 1 && !visited[j + 1][i])
                    solved = DFS(board, visited, result, cur, current, i, j + 1);
                if(!solved && j > 0 && !visited[j - 1][i])
                    solved = DFS(board, visited, result, cur, current, i, j - 1);
                
                if(solved){
                    if(pre == root){
                        pre->child = cur->next;
                    }else{
                        pre->next = cur->next;
                    }
                    Trie* toBeDeleted = cur;
                    cur = cur->next;
                    delete toBeDeleted;
                }else{
                    pre = cur;
                    cur = cur->next;
                }
            }
            visited[j][i] = false;
            //if root->child == NULL, then all children have been solved and deleted, thus return true
            return (root->child == NULL);
        }
    }
    
    vector<string> findWords(vector<vector<char>>& board, vector<string>& words) {
        vector<string> result;
        height = (int)board.size();
        if(height == 0){
            return result;
        }
        width = (int)board[0].size();
        
        Trie* root = new Trie('$');
        for(auto& word : words){
            addWord(root, word);
        }
        vector<vector<bool>> visited(height, vector<bool>(width, false));
        
        Trie* toBeDeleted = root;
        root = root->child;
        delete toBeDeleted;
        
        while(root){
            int solved = false;
            for(int j = 0; j < height && !solved; ++j){
                for(int i = 0; i < width && !solved; ++i){
                    solved = DFS(board, visited, result, root, "", i, j);
                }
            }
            toBeDeleted = root;
            root = root->next;
            delete toBeDeleted;
        }
        
        return result;
    }
};

