#include "inc.h"


class Solution {
    vector<vector<int>> dirs = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
    int m, n;
    vector<int> dist;
public:
    int shortestDistance(vector<vector<int>>& maze, vector<int>& start, vector<int>& destination) {
        if(maze.empty() || maze[0].empty()){
            return -1;
        }
        m = (int)maze.size();
        n = (int)maze[0].size();
        dist.assign(m * n, -1);
        return Dijkstra(maze, start[0] * n + start[1], destination[0] * n + destination[1]);
    }
    
    int Dijkstra(vector<vector<int>>& maze, int start, int dest){
        dist[start] = 0;
        auto cmp = [&](int a, int b) -> bool{
            return dist[a] == dist[b]? a < b : dist[a] < dist[b];
        };
        multiset<int, decltype(cmp)> fringe(cmp);
        fringe.insert(start);
        while(!fringe.empty()){
            int current = *fringe.begin();
            if(current == dest){
                break;
            }
            fringe.erase(fringe.begin());
            auto neighbors = getNeighbors(maze, current);
            for(auto neighbor : neighbors){
                if(dist[neighbor.first] < 0){   //new unseen node
                    dist[neighbor.first] = dist[current] + neighbor.second;
                    fringe.insert(neighbor.first);
                }else if(dist[neighbor.first] > dist[current] + neighbor.second){
                    fringe.erase(neighbor.first);
                    dist[neighbor.first] = dist[current] + neighbor.second;
                    fringe.insert(neighbor.first);
                }
            }
        }
        return dist[dest];
    }
    
    vector<pair<int, int>> getNeighbors(vector<vector<int>>& maze, int node){
        vector<pair<int, int>> result;
        for(int i = 0; i < 4; ++i){
            auto neighbor = goToEnd(maze, node, dirs[i]);
            if(neighbor.second > 0){
                result.push_back(neighbor);
            }
        }
        return result;
    }
    pair<int, int> goToEnd(vector<vector<int>>& maze, int node, vector<int> dir){
        int i = node / n;
        int j = node % n;
        int len = 0;
        while(i + dir[0] >= 0 && i + dir[0] < m && j + dir[1] >= 0 && j + dir[1] < n && maze[i + dir[0]][j + dir[1]] == 0){
            i += dir[0];
            j += dir[1];
            len++;
        }
        return make_pair(i * n + j, len);
    }
};



